function NeedReflect(unitEnemy, unitFreind) if UnitIsEnemy("player", unitEnemy) and UnitIsUnit(unitFreind, unitEnemy.."target") then local name,_,_,_,_,endTime = UnitCastingInfo(unitEnemy); if name and (((endTime / 1000) - GetTime()) - AddonFrame.ping) < 2 then if UnitIsPVP("player") then for _,spell_id in ipairs(reflect_table.PVP) do if name == GetSpellInfo(spell_id) then return true; end end else for _,spell_id in ipairs(reflect_table.RAIDS) do if name == GetSpellInfo(spell_id) then return true; end end end end end end
function NeedMassReflect() local reflect_buff = { 23920, 114028 }; if not HasBuff("player", reflect_buff) or SpellCD(23920) > 0 then if IsInArenaTeam() then for i = 1, 5 do for j = 1, 5 do if UnitExists("party"..i) and UnitExists("arena"..j) then return NeedReflect("party"..i, "arena"..j); end end end else return NeedReflect("target", "player"); end end end

