        else
            isAltMod = true;
            AddonFrame.print("|cff00ff00Используется непроницаемый щит", true);
        end
    elseif modifier == "RSHIFT" then
        if useCooldown then
            useCooldown = false;
            AddonFrame.print("|cffff0000Кулдауны выключены", true);
        else
            useCooldown = true;
            AddonFrame.print("|cff00ff00Кулдауны включены", true);
        end
    end
end

