DEBUFF_LIST  = { };

DISPELL_LIST = {
    --  (Хоридон ХМ)
    { Id = 136587, Count = 0, Rem = 0, BossId = 0 }, -- Залп ядовитых стрел
    { Id = 136719, Count = 0, Rem = 0, BossId = 0 }, -- Пылающий свет
    { Id = 136710, Count = 6, Rem = 0, BossId = 0 }, -- Смертельная чума
    { Id = 136512, Count = 0, Rem = 0, BossId = 0 }, -- Проклятие растерянности
    -- Совет старейшин
    { Id = 0, Count = 0, Rem = 0, BossId = 0 }, --
};

function CheckNotHeal(unit, standartSpellId)
    for i,v in ipairs(DEBUFF_LIST) do
        if HasDebuff(unit, v) then
            return true;
        end
    end
end

function CheckDispell(unit)
    for _, debuff in ipairs(DISPELL_LIST) do
        local name, count, rem = HasDebuff(unit, debuff.Id);
        if (debuff.Count > 0 and (count or 0) >= debuff.Count) then
            return true;
        end
        if (debuff.Rem > 0 and (rem or 0) <= debuff.Rem) then
            return true;
        end
        if name then
            return true;
        end
    end
end

-- Получает информацию
function UpdatePartyTables(standartSpellId)

    LowHP = { H90 = 0, H80 = 0, H70 = 0, H60 = 0, H50 = 0, H40 = 0, H30 = 0, H20 = 0 };

    -- Если поменялось количество, либо же не доступна одна из таблиц - пересоздадим
    if (type(members) ~= "table" or GetNumGroupMembers() ~= #members) then

        members = { { Unit = "player", Name = UnitName("player"), Class = UnitClass("player"), Role = "HEAL" } };

        local prefix = "party";

        if IsInRaid() then
            prefix = "raid";
        elseif IsInArenaTeam() then
            prefix = "arena";
        end

        for index = 1, GetNumGroupMembers() do
            local name,rank,subgroup,level,class,_,zone,online,isDead,role = GetRaidRosterInfo(index);
            local unit = string.format("%s%i", prefix, index);

            if UnitExists(unit) and UnitGUID(unit) ~= UnitGUID("player") then
                table.insert(members, { Unit = unit, Name = name, Class = class, Role = role });
            end
        end
    end

    local px, py = GetPlayerMapPosition("player");
    -- Обновляем статиы игроков из пати/рейда
    for i, member in ipairs(members) do
        if UnitExists(member.Unit) then

            member.IsDead      = UnitIsDeadOrGhost(member.Unit);
            member.Agro        = UnitThreatSituation(member.Unit) or 0;
            member.IsOnline    = 0;

            local X,Y          = GetPlayerMapPosition(member.Unit);
            local hpcur        = UnitHealth(member.Unit) or 1;
            local hpmax        = UnitHealthMax(member.Unit) or 1;

            member.X, member.Y = X or 0, Y or 0;
            member.Range       = GetDistance2(px, py, X, Y);
            member.IsInRange   = IsSpellInRange(standartSpellId) ~= 1;

            member.HP          = 100 * hpcur / hpmax;
            member.HPMAX       = hpmax;
            member.HPI         = hpcur;
            if member.IsInRange then
                member.DontHeal    = CheckNotHeal(member.Unit, standartSpellId);
                member.NeedDispell = false
            end

            if not member.IsDead and member.HP < 90 then LowHP.H90 = LowHP.H90 + 1; end
            if not member.IsDead and member.HP < 80 then LowHP.H80 = LowHP.H80 + 1; end
            if not member.IsDead and member.HP < 70 then LowHP.H70 = LowHP.H70 + 1; end
            if not member.IsDead and member.HP < 60 then LowHP.H60 = LowHP.H60 + 1; end
            if not member.IsDead and member.HP < 50 then LowHP.H50 = LowHP.H50 + 1; end
            if not member.IsDead and member.HP < 40 then LowHP.H40 = LowHP.H40 + 1; end
            if not member.IsDead and member.HP < 30 then LowHP.H30 = LowHP.H30 + 1; end
            if not member.IsDead and member.HP < 20 then LowHP.H20 = LowHP.H20 + 1; end
        end
    end

    -- сортируем таблицы по наименьшему количеству ХР
    if #members > 0 then
        table.sort(members, function(x, y)
            if (x.IsDead or not x.IsInRange) then
                return false;
            end
            return x.HP < y.HP;
        end);
    end
end

