ShadowWordPainT = nil;
ShadowWordPainM = nil;
ShadowWordPainF = nil;

-- список проков, на которые надо реагировать
AURA_LIST = {
};

hooksecurefunc("AddonFrame_OnEvent", function (self, event, ...)

    if event == "COMBAT_LOG_EVENT_UNFILTERED" then
        -- для начала приведу полный список параметров.
        local timestamp, subEvent, hideCaster,
                sourceGUID,srcName,  sourceFlags,sourceFlags2,
                destGUID,  destName, destFlags,  destFlags2,
                spellId,   spellName,spellSchool,amount,      overkill,school,
                resisted,  blocked,  absorbed,   critical,    glancing,crushing = ...;

        if subEvent == "SPELL_AURA_APPLIED" and destGUID == UnitGUID("player") then
            for i,aura_id in ipairs(AURA_LIST) do
                if aura_id == spellId then
                    VampTouchT      = true;
                    VampTouchM      = true;
                    VampTouchF      = true;
                    ShadowWordPainT = true;
                    ShadowWordPainM = true;
                    ShadowWordPainF = true;
                    break;
                end
            end
        end

        if subEvent == "SPELL_CAST_SUCCESS" and sourceGUID == UnitGUID("player") then
            if spellId == 34914 then --Прикосновение вампира
                if UnitGUID("target") == destGUID then
                    VampTouchT = nil;
                elseif UnitGUID("fucus") == destGUID then
                    VampTouchF = nil;
                elseif UnitGUID("mouseover") == destGUID then
                    VampTouchM = nil;
                end
            elseif spellId == 589 then --Слово тьмы: Боль
                if UnitGUID("target") == destGUID then
                    ShadowWordPainT = nil;
                elseif UnitGUID("fucus") == destGUID then
                    ShadowWordPainF = nil;
                elseif UnitGUID("mouseover") == destGUID then
                    ShadowWordPainM = nil;
                end
            end
        end
    end
end);

