if type(members) ~= "table" or type(tanks) ~= "table"
    or GetNumGroupMembers() ~= (#members + #tanks) then

    members = { };
    tanks   = { }; -- таблица танков

    local prefix = "party";
    if IsInRaid() then
        prefix = "raid";
    end

    for i = 1, GetNumGroupMembers() do
        local name, rank, subgroup, level, class, fileName, zone, online, isDead, role, isML = GetRaidRosterInfo(i);
        local unit = prefix..i;
        if UnitExists(unit) then
            local hpcur = UnitHealth(unit) or 1;
            local hpmax = UnitHealthMax(unit) or 1;

            if role == "TANK" then
                table.insert(members, {
                    Unit    = unit,
                    Name    = name,
                    Class   = class,
                    IsActive= false,
                    HP      = 100 * hpcur / hpmax,
                    HPMAX   = hpmax,
                    HPI     = hpcur,
                    X,Y     = GetPlayerMapPosition(unit),
                    IsDead  = isDead;
                });
            else
                table.insert(members, {
                    Unit    = unit,
                    Name    = name,
                    Class   = class,
                    Role    = 0,
                    HP      = 100 * hpcur / hpmax,
                    HPMAX   = hpmax,
                    HPI     = hpcur,
                    X,Y     = GetPlayerMapPosition(unit),
                    IsDead  = isDead;
                });
            end
        end
    end

end

PlayerHp90 = 0;
PlayerHp70 = 0;
PlayerHp50 = 0;
PlayerHp30 = 0;

-- Обновляем статиы играков из пати/рейда
for i, member in ipairs(members) do
    if UnitExists(member.Unit) then

        member.IsDead  = UnitIsDeadOrGhost(unit);

        local hpcur = UnitHealth(unit) or 1;
        local hpmax = UnitHealthMax(unit) or 1;

        if member.IsDead then
            member.HP = 0xffff;
            member.HPMAX   = 0;
            member.HPI     = 0;
        else
            member.HP      = 100 * hpcur / hpmax;
            member.HPMAX   = hpmax;
            member.HPI     = hpcur;
        end

        member.X, member.Y = GetPlayerMapPosition();

        if member.HP < 90 then PlayerHp90 = PlayerHp90 + 1; end
        if member.HP < 70 then PlayerHp70 = PlayerHp70 + 1; end
        if member.HP < 50 then PlayerHp50 = PlayerHp50 + 1; end
        if member.HP < 30 then PlayerHp30 = PlayerHp30 + 1; end
    end
end

-- в основном нам надо следить за позицией танков, их здоровьенм и тем кто сейчас активен.
for i, member in ipairs(tanks) do
    if UnitExists(member.Unit) then

        member.IsDead  = UnitIsDeadOrGhost(unit);

        local hpcur = UnitHealth(unit) or 1;
        local hpmax = UnitHealthMax(unit) or 1;

        if member.IsDead then
            member.HP = 0xffff;
            member.HPMAX   = 0;
            member.HPI     = 0;
        else
            member.HP      = 100 * hpcur / hpmax;
            member.HPMAX   = hpmax;
            member.HPI     = hpcur;
        end

        member.IsActive = false;    -- Надо вычислять активного (получающего урон танка)
        member.X, member.Y = GetPlayerMapPosition();
    end
end

-- сортируем таблицы по наименьшему количеству ХР
if #members > 0 then
    table.sort(members, function(x, y)
        return x.HP < y.HP;
    end);
end

