hooksecurefunc("AddonFrame_OnEvent", function (self, event, ...)
if event == "COMBAT_LOG_EVENT_UNFILTERED" then -- для начала приведу полный список параметров. local timestamp, subEvent, hideCaster, sourceGUID,srcName, sourceFlags,sourceFlags2, destGUID, destName, destFlags, destFlags2, spellId, spellName,spellSchool,amount, overkill,school, resisted, blocked, absorbed, critical, glancing,crushing = ...;
if subEvent == "SPELL_PERIODIC_DAMAGE" then if UnitName("player") == srcName and destName == UnitName("target") then -- Воспламенение if spellId == 12654 then IgniteDamage = amount; --print("Тик: Воспламенение (12654) - "..tostring(amount)); end end end
if subEvent == "SPELL_DAMAGE" then if UnitName("player") == srcName and destName == UnitName("target") then -- Огненная глыба if spellId == 11366 then pyroDamage = amount; pyroCrit = critical; --print("Урон: "..tostring(critical).." Огненная глыба (11366) - "..tostring(amount)); end end end end end);

